// Configure Spark
val cfg = new SparkConf()
  .setAppName("keyValueExample")
  .setMaster("local[*]")
  .set("com.couchbase.bucket.travel-sample", "")

// Generate The Context
val sc = new SparkContext(cfg)

// Spark SQL Setup
val sql = new SQLContext(sc)

// Load Landmarks from HDFS val landmarks = sql.read.json("hdfs://127.0.0.1:9000/landmarks/*") landmarks.registerTempTable("landmarks")
// Load Airports from Couchbase val airports = sql.read.couchbase(schemaFilter = EqualTo("type", "airport"))
// find all landmarks in the same city as the given FAA code val toFind = "SFO" // try SFO or LAX
airports .join(landmarks, airports("city") === landmarks("city")) .select(airports("faa"), landmarks("name"), landmarks("url")) .where(airports("faa") === toFind and landmarks("url").isNotNull) .orderBy(landmarks("name").asc) .show(20)

val df = sql.read
  // Define the source, required
  .format("com.couchbase.spark.sql.DefaultSource")

  // Either set the schema filter for inference. Like this
  .option("schemaFilter", N1QLRelation.filterToExpression(EqualTo("type", "airline")))
  // Or directly as a string
  .option("schemaFilter", "`type` = 'airline'")
  // Or provide the schema directly
  .schema(StructType(
    StructField("name", StringType):: Nil
  ))

  .load()
