// Initialize Node curl -u Administrator:password -v -X POST http://[localhost]:8091/nodes/self/controller/settings -d path=[location] -d index_path=[location]
// Rename Node curl -u Administrator:password -v -X POST http://[localhost]:8091/node/controller/rename -d hostname=[localhost]
// Setup Services curl -u Administrator:password -v -X POST http://[localhost]:8091/node/controller/setupServices -d services=[kv | index | n1ql | fts]
// Setup Administrator username and password curl -u Administrator:password -v -X POST http://[localhost]:8091/settings/web -d password=[password] -d username=[admin-name] -d port=8091
// Setup Bucket curl -u Administrator:password -v -X POST http://[localhost]:8091/pools/default/buckets -d flushEnabled=[1 | 0] -d replicaNumber=[0 - n] -d evictionPolicy=[valueOnly | full] -d ramQuotaMB=[value] -d bucketType=[membase | couchbase] -d name=[bucket-name] -d authType=[none | sasl] -d saslPassword=[password]

