rows, err := myBucket.ExecuteViewQuery(myQuery)
             if err != nil {
             fmt.Printf("View query error: %s\n", err)
             }

             var row interface{}
             for rows.Next(&row) {
             fmt.Printf("Row: %+v\n", row)
             }
             if err := rows.Close(); err != nil {
             fmt.Printf("View query results error: %s\n", err)
             }
