function (doc, meta) {
  if (doc.type && doc.type == "beer" && doc.name) {
     emit(doc.name, null);
  }
}

function (doc, meta) {
    if (doc.type && doc.type == "landmark" && doc.name) {
        emit(doc.name, null);
    }
}

function (doc) {
    if (doc.type && doc.type == "landmark" && doc.geo) {
        emit([doc.geo.lon, doc.geo.lat], null);
    }
}
