section.codeblock:
	switch r.Method {
		case "GET":
			// login request for existing user
			q.User = r.URL.Query().Get("user")
			q.Password = r.URL.Query().Get("password")
			if authenticated:=q.LoginUser(); authenticated==true{
				s.Success=q.Token
				bytes,_:=json.Marshal(s)
				w.Write(bytes)
			}else{
				bytes:=[]byte(`{"failure":"Bad Username or Password"}`)
				w.Write(bytes)
			}
		case "POST":
			// login request for a new user
			_ = json.NewDecoder(r.Body).Decode(&q)
			if exists := q.CheckUserExists(); exists == true {
				bytes:=[]byte(`{"failure":"User exists, please choose a different username"}`)
				w.Write(bytes)
			}
			if created := q.CreateUser(); created == true {
				s.Success=q.Token
				bytes,_:=json.Marshal(s)
				w.Write(bytes)
			}
		}
}

func (u *UserIntermediary) CheckUserExists() bool{
	var curUser User
	if _,err := bucket.Get(u.User,&curUser); err != nil{
		return false
	}
	return true
}

func (u *UserIntermediary) CreateUser() bool{
	token := jwt.New(jwt.SigningMethodHS256)
	token.Claims["user"] = u.User
	if encryptedToken, err := token.SignedString([]byte(hashToken)); err != nil{
		return false
	} else {
		u.Token=encryptedToken
	}

	var newUser User
	newUser.Type = "User"
	newUser.ID = "NOT_CURRENTLY_USED"
	newUser.Name = u.User
	newUser.Password = u.Password
	newUser.Token = u.Token
	if _, err := bucket.Insert(newUser.Name, newUser, 0); err != nil{
			return false
	}
	return true
}

var queryPrep string

switch search := r.URL.Query().Get("search"); len(search) {
case 3:
	queryPrep = "SELECT airportname FROM `travel-sample` WHERE faa ='" + strings.ToUpper(search) + "'"
case 4:
	if s:=strings.ToUpper(search); s==search {
		queryPrep = "SELECT airportname FROM `travel-sample` WHERE icao ='" + strings.ToUpper(search) + "'"
	}else{
		queryPrep = "SELECT airportname FROM `travel-sample` WHERE airportname like '" + search + "%'"
	}
default:
	queryPrep = "SELECT airportname FROM `travel-sample` WHERE airportname like '" + search + "%'"
}

func flightPathHandler(w http.ResponseWriter, r *http.Request) {
var queryPrep, queryTo, queryFrom string var fromLon, fromLat, toLon, toLat, dist float64 var price, flightTime, weekday int var leave time.Time var row AirportIntermediary var airports []AirportIntermediary var flight Flight var flights []Flight
from := r.URL.Query().Get("from") to := r.URL.Query().Get("to")
leave,_ = time.Parse(layout,r.URL.Query().Get("leave")) weekday = int(leave.Weekday())+1
queryPrep = "SELECT faa as fromAirport,geo FROM `travel-sample` WHERE airportname = '" + from + "' UNION SELECT faa as toAirport,geo FROM `travel-sample` WHERE airportname = '" + to + "'"
myQuery := gocb.NewN1qlQuery(queryPrep) rows,err := bucket.ExecuteN1qlQuery(myQuery,nil) if err!=nil{ fmt.Println("ERROR EXECUTING N1QL QUERY:",err) }
for rows.Next(&row) { airports = append(airports,row) if row.ToAirport!="" { toLat=row.Geo.Lat toLon=row.Geo.Lon queryTo=row.ToAirport } if row.FromAirport!="" { fromLat=row.Geo.Lat fromLon=row.Geo.Lon queryFrom=row.FromAirport } row = AirportIntermediary{} } dist = Haversine(fromLon,fromLat,toLon,toLat) flightTime = int(dist/averageKilometersHour) price = int(dist * distanceCostMultiplier)
_ = rows.Close()
queryPrep="SELECT r.id, a.name, s.flight, s.utc, r.sourceairport, r.destinationairport, r.equipment " + "FROM `travel-sample` r UNNEST r.schedule s JOIN `travel-sample` a ON KEYS r.airlineid WHERE r.sourceairport='" + queryFrom + "' AND r.destinationairport='" + queryTo + "' AND s.day=" + strconv.Itoa(weekday) + " ORDER BY a.name"
myQuery = gocb.NewN1qlQuery(queryPrep) rows,err = bucket.ExecuteN1qlQuery(myQuery,nil) if err!=nil{ fmt.Println("ERROR EXECUTING N1QL QUERY:",err) }
for i:=0; rows.Next(&flight);i++ { flight.Flighttime=flightTime flight.Price=price flights=append(flights,flight) } _ = rows.Close() bytes,_:=json.Marshal(flights) w.Write(bytes) }
